INSERT INTO `category` (`no`, `title`, `introduce`, `del_yn`, `id`, `click`, `pref`, `type`, `img`, `reg_date`, `auth`) VALUES
	(35, '천하에 묻다.', '천하에 묻다.', 'N', 'Yaoon', 4, 0, '무협', 'resources/img/QUAI DE I\'ILE-top.png', '2018-01-22 14:42:39', 2),
	(36, '까마귀 두령', '까마귀 두령', 'N', 'Yaoon', 1, 1, '무협', 'resources/img/QUAI DE I\'ILE-top.png', '2018-01-22 14:42:39', 2),
	(37, '속삭이는 바람', '속삭이는 바람이 휘몰아치고, 자체로 불길하게 움직였따.', 'N', 'Yaoon', 3, 0, '무협', 'resources/img/img_basic.jpg', '2018-01-22 14:42:39', 2),
	(38, '초콜릿을 뺏지 마', '[상큼한 첫사랑] [달달] [럭셔리] [미스터리] [판타지]', 'N', 'Yaoon', 2, 0, '판타지', 'resources/img/img_basic.jpg', '2018-01-22 14:42:39', 2),
	(39, '변장공주 개정판', '세상에서 가장 아름다운 잉글랜드의 에반젤린 공주가 자신이 늙어도 ... ', 'N', 'Yaoon', 3, 0, '로맨스 판타지', 'resources/img/img_basic.jpg', '2018-01-22 14:42:39', 2),
	(40, '아무것도 아닌 복수', '#처음에는 가벼운 내용이다가 점점 진실에 가까워지는 이야기', 'N', 'Yaoon', 5, 1, '로맨스 판타지', 'resources/img/img_basic.jpg', '2018-01-22 14:42:39', 2),
	(41, '사랑하는 퇴마사님', '낮에는 소설가 밤에는 퇴마사 두 여자와 동거하는 명우의 좌충우돌 사랑이야기', 'N', 'Yaoon', 4, 0, '로맨스 판타지', 'resources/img/img_basic.jpg', '2018-01-22 14:42:39', 2),
	(42, '마지막 마녀의 심장', '기억을 담은 심장과 그 심장을 잃어버린 마지막 마녀', 'N', 'Yaoon', 1, 0, '로맨스', 'resources/img/img_basic.jpg', '2018-01-22 14:42:39', 2),
	(43, '달과 나비와 계수나무', '자비국의 태자<계>.\r\n상처를 숨기고 무력을 앞세우던 그는 모종의 사건으로 폐위당하고 전쟁터를...', 'N', 'Yaoon', 0, 0, '로맨스', 'resources/img/img_basic.jpg', '2018-01-22 14:42:39', 2),
	(44, '기약', '그대를 몰랐던 나날이 아쉬워 미칠것만 같다. 그래서 이제라도 조금 더 ...', 'N', 'Yaoon', 0, 0, '로맨스', 'resources/img/img_basic.jpg', '2018-01-22 14:42:39', 2),
	(45, '검은 양과 흰 늑대', '양은 늑대를 만나 그와 사랑에 빠졌답니다.', 'N', 'chomyang', 12, 1, '추리', 'resources/img/img_basic.jpg', '2018-01-25 14:24:28', 1),
	(46, '살인 공방', '세계에서 일어나는 엽기 사건의 근원지인 \' 살인공방에\' 우연히...', 'N', 'chomyang', 6, 0, '추리', 'resources/img/img_basic.jpg', '2018-01-22 14:42:39', 1),
	(47, '동화 카페', '맛있는 케이크와 커피를 만들어내는 곳으로 유명한 카페 \'동화\'', 'N', 'chomyang', 9, 0, '추리', 'resources/img/img_basic.jpg', '2018-01-22 14:42:39', 1),
	(48, '사랑,설계해드릴까요?', '대신 나간 선자리에서 만남 남자. 그 남자를 다시 만날줄은 몰랐다.', 'N', 'chomyang', 0, 0, '로맨스', 'resources/img/img_basic.jpg', '2018-01-22 14:42:39', 1),
	(49, '너만 보면 자꾸 떨려와', '오빠 둘 여동생 하나인 집안의 여동생을 맡고 있는 성스런', 'N', 'chomyang', 0, 0, '로맨스', 'resources/img/img_basic.jpg', '2018-01-22 14:42:39', 1),
	(50, '상속녀의 남자', '한날 한시 사고로 부모님을 잃은 대일그룹 상속녀. 유세희와 아버지를 잃은...', 'N', 'chomyang', 0, 0, '로맨스', 'resources/img/img_basic.jpg', '2018-01-22 14:42:39', 1),
	(51, '서울쉐어하우스', '조실부모 알바인생 윤이나와 결벽증 모태솔로 한설주가 외모만 보면 완벽한 남자 하숙인..', 'N', 'Yaoon', 0, 0, '추리', 'resources/img/img_basic.jpg', '2018-01-22 14:42:39', 2),
	(52, '조선공주야사', '모든것의 시작은 \'여인도\' 그 그림이었다.', 'N', 'Yaoon', 0, 0, '판타지', 'resources/img/img_basic.jpg', '2018-01-22 14:42:39', 2),
	(53, '전생', '출신이 다른 두 사람의 지난 이야기', 'N', 'Yaoon', 0, 0, '추리', 'resources/img/img_basic.jpg', '2018-01-22 14:42:39', 2),
	(54, '가짜신부', '쌍둥이 자매의 남편만 모르는 아슬아슬한 위장 신혼 이야기', 'N', 'Yaoon', 0, 0, '추리', 'resources/img/img_basic.jpg', '2018-01-22 14:42:39', 1),
	(55, '히드라의 서', '히드라의 서 웃음 저녁의 현대 판타지', 'N', 'chomyang', 1, 0, '판타지', 'resources/img/img_basic.jpg', '2018-01-22 14:42:39', 1),
	(56, '하모니', '마법학교를 막 졸업한 말괄량이 소녀 \'타니아\'의 모험 이야기', 'N', 'chomyang', 0, 0, '판타지', 'resources/img/img_basic.jpg', '2018-01-22 14:42:39', 1),
	(57, '언령사', '언어의 영능으로 기적을 이루어내는 세계', 'N', 'chomyang', 0, 0, '무협', 'resources/img/img_basic.jpg', '2018-01-22 14:42:39', 1),
	(58, '파촉지룡', '태어나기도 전에 모든 것을 잃었다.', 'N', 'chomyang', 0, 0, '무협', 'resources/img/img_basic.jpg', '2018-01-22 14:42:39', 1),
	(59, '태을검제', '악덕 사부와 노예 제자 유천이 만나다.', 'N', 'chomyang', 0, 0, '무협', 'resources/img/img_basic.jpg', '2018-01-22 14:42:39', 1),
	(60, '사로잡히다', '남자는 사랑에 사로잡히고, 여자는 사람에 사로잡히다', 'N', 'chomyang', 0, 0, '로맨스 판타지', 'resources/img/img_basic.jpg', '2018-01-22 14:42:39', 1),
	(61, '아도니아', '오래 전 가난한 남작부부의 어린 아들에게 구출되어 부부의 수양딸...', 'N', 'chomyang', 0, 0, '로맨스 판타지', 'resources/img/img_basic.jpg', '2018-01-22 14:42:39', 1),
	(62, '소원의 끝에서', '소원자 때문에 모든 것을 잃은 서지안. 삶이 끝나기 직전..', 'N', 'chomyang', 0, 0, '로맨스 판타지', 'resources/img/img_basic.jpg', '2018-01-22 14:42:39', 1),
	(63, '공략대', '이름부터 낯선 차원 전쟁. ', 'N', 'chomyang', 0, 0, '판타지', 'resources/img/img_basic.jpg', '2018-01-22 14:42:39', 1),
	(64, '데브리스트', '망상증이 있는 탐사대원 소년의 이세계 탐사기록', 'N', 'Yaoon', 0, 0, '판타지', 'resources/img/img_basic.jpg', '2018-01-22 14:42:39', 1),
	(70, 'ㅎㅇㅎㅇ', 'ㅎㅇㅎㅇ', 'N', 'gudi', 999, 0, '로맨스 판타지', 'resources/img/제네바 홀마크.png', '2018-01-23 13:23:33', 2),
