-- test 데이터베이스 구조 내보내기
CREATE DATABASE IF NOT EXISTS `test` /*!40100 DEFAULT CHARACTER SET latin1 */;
USE `test`;

-- 테이블 test.category 구조 내보내기
CREATE TABLE IF NOT EXISTS `category` (
  `no` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(100) NOT NULL,
  `introduce` varchar(100) NOT NULL,
  `del_yn` varchar(1) NOT NULL DEFAULT 'N',
  `id` varchar(10) DEFAULT NULL,
  `click` int(8) DEFAULT '0',
  `pref` int(8) DEFAULT '0',
  `type` varchar(50) DEFAULT NULL,
  `img` varchar(500) DEFAULT 'resources/img/clock.png',
  `reg_date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `auth` int(11) NOT NULL,
  PRIMARY KEY (`no`)
) ENGINE=InnoDB AUTO_INCREMENT=146 DEFAULT CHARSET=utf8 COMMENT='pref - 선호작 \r\nclick - 조회수 \r\ntype - 1 판타지 2 무협 3 ... \r\nauth - 1 작가 2 일반';

